package com.qtz.sm.supermarket.page;import com.mall.core.vo.Pager;/** * <p> * Title:SupermarketCategoryPage * </p> * <p> * Description:超市类目分页类 * </p> * <p> * Copyright: Copyright (c) 2016 * </p> * <p> * Company: 深圳擎天柱信息技术有限公司 * </p> *  * @author 刘晓峰 - Laven * @version v1.0 2016-05-10 */public class SupermarketCategoryPage extends Pager<com.qtz.sm.supermarket.vo.SupermarketCategory, Long>        implements java.io.Serializable {            /** 类的版本号 */    private static final long serialVersionUID = 1656365854754817L;        /** 主键ID */    private Long dmId;    /** 所属超市ID */    private Long supermarketId;    /** 父类目ID */    private Long pid;    /** 类目名称 */    private String name;    /** 类目图标 */    private String icon;    /** 层级(1,2,3....) */    private Integer level;    /** 排序 */    private Integer sort;    /** 状态 */    private Integer status;    /**  */    private java.util.Date updateTime;    /**  */    private java.util.Date createTime;    /** 纬度 */    private double latitude;    /** 经度 */    private double longitude;        /** 分页查询条件 */    private String condition;        /**仓储中心id*/    private long cczxId;        public Long getDmId() {        return this.dmId;    }        public void setDmId(Long dmId) {        this.dmId = dmId;    }        public Long getSupermarketId() {        return this.supermarketId;    }        public void setSupermarketId(Long supermarketId) {        this.supermarketId = supermarketId;    }        public Long getPid() {        return this.pid;    }        public void setPid(Long pid) {        this.pid = pid;    }        public String getName() {        return this.name;    }        public void setName(String name) {        this.name = name;    }        public String getIcon() {        return this.icon;    }        public void setIcon(String icon) {        this.icon = icon;    }        public Integer getLevel() {        return this.level;    }        public void setLevel(Integer level) {        this.level = level;    }        public Integer getSort() {        return this.sort;    }        public void setSort(Integer sort) {        this.sort = sort;    }        public Integer getStatus() {        return this.status;    }        public void setStatus(Integer status) {        this.status = status;    }        public java.util.Date getUpdateTime() {        return this.updateTime;    }        public void setUpdateTime(java.util.Date updateTime) {        this.updateTime = updateTime;    }        public java.util.Date getCreateTime() {        return this.createTime;    }        public void setCreateTime(java.util.Date createTime) {        this.createTime = createTime;    }        public double getLatitude() {        return latitude;    }        public void setLatitude(double latitude) {        this.latitude = latitude;    }        public double getLongitude() {        return longitude;    }        public void setLongitude(double longitude) {        this.longitude = longitude;    }        public String getCondition() {        return condition;    }        public void setCondition(Integer pageSize, Integer pageNum) {        int start = (pageNum - 1) * pageSize <= 0 ? 0 : (pageNum - 1) * pageSize;        this.condition = " limit " + start + "," + pageSize;    }	public long getCczxId() {		return cczxId;	}	public void setCczxId(long cczxId) {		this.cczxId = cczxId;	}	public String toString() {        return "SupermarketCategoryPage [dmId=" + dmId + ", supermarketId=" + supermarketId + ", pid=" + pid + ", name="                + name + ", icon=" + icon + ", level=" + level + ", sort=" + sort + ", status=" + status                + ", updateTime=" + updateTime + ", createTime=" + createTime + ", latitude=" + latitude                + ", longitude=" + longitude + ", condition=" + condition + "]";    }}